        if (PlayVod_jumpStepsIncreaseLock) {
            if (PlayVod_jumpCount < Play_DefaultjumpTimers.length - 1) {
                PlayVod_jumpCount++;
            } else PlayVod_jumpCount = 0;
        }

        PlayVod_jumpStepsIncreaseLock = true;
