    function PlayVod_NumberKey_QuickJump(key) {
        if (Play_isEndDialogVisible() || Play_isVodDialogVisible()) return;

        var position = null,
            defaultTimeout = 2500;

        switch (key) {
            case KEY_NUMPAD_0:
            case KEY_0:
                position = 0;
                break;
            case KEY_NUMPAD_1:
            case KEY_1:
                position = 1;
                break;
            case KEY_NUMPAD_2:
            case KEY_2:
                position = 2;
                break;
            case KEY_NUMPAD_3:
            case KEY_3:
                position = 3;
                break;
            case KEY_NUMPAD_4:
            case KEY_4:
                position = 4;
                break;
            case KEY_NUMPAD_5:
            case KEY_5:
                position = 5;
                break;
            case KEY_NUMPAD_6:
            case KEY_6:
                position = 6;
                break;
            case KEY_NUMPAD_7:
            case KEY_7:
                position = 7;
                break;
            case KEY_NUMPAD_8:
            case KEY_8:
                position = 8;
                break;
            case KEY_NUMPAD_9:
            case KEY_9:
                position = 9;
                break;
            default:
                break;
        }

        if (position !== null && PlayVod_NumberKeyId && PlayVod_NumberKeyOldKey === key) {
            PlayVod_NumberKey_QuickJumpClear();

            PlayVod_TimeToJump = (Play_DurationSeconds / 10) * position;

            PlayVod_TimeToJump = (Play_DurationSeconds / 10) * position;
            Play_showWarningDialog(
                STR_JUMP_TIME +
                    STR_SPACE_HTML +
                    STR_JUMP_T0 +
                    STR_SPACE_HTML +
                    position * 10 +
                    '% (' +
                    Play_timeS(PlayVod_TimeToJump) +
                    ')' +
                    STR_BR +
                    STR_FROM +
                    Play_timeMs(OSInterface_gettime()),
                defaultTimeout
            );

            PlayVod_jump();
        } else if (position !== null) {
            //show warning then jump if click again
            PlayVod_NumberKeyOldKey = key;

            Play_showWarningDialog(
                STR_JUMP_TIME_CLICK_AGAIN +
                    STR_SPACE_HTML +
                    STR_JUMP_T0 +
                    STR_SPACE_HTML +
                    position * 10 +
                    '% (' +
                    Play_timeS((Play_DurationSeconds / 10) * position) +
                    ')' +
                    STR_BR +
                    STR_FROM +
                    Play_timeMs(OSInterface_gettime()),
                defaultTimeout
            );

            PlayVod_NumberKeyId = Main_setTimeout(
                function () {
                    PlayVod_NumberKey_QuickJumpClear();
                },
                defaultTimeout,
                PlayVod_NumberKeyId
            );
        }
    }

    function PlayVod_NumberKey_QuickJumpClear() {
        Main_clearTimeout(PlayVod_NumberKeyId);
        PlayVod_NumberKeyId = null;
        PlayVod_NumberKeyOldKey = null;
    }

