    function Settings_GenerateClock() {
        var clock = [],
            time = 43200,
            i = 0;

        for (i; i < 48; i++) {
            clock.push('-' + Play_timeS(time));
            time -= 900;
        }

        clock.push(Play_timeS(0));
        time = 900;

        for (i = 0; i < 48; i++) {
            clock.push(Play_timeS(time));
            time += 900;
        }

        return clock;
    }

    function Settings_GetVolumes() {
        var array = [],
            i = 0;

        for (i = 0; i < 101; i += Settings_VolumeScale) {
            array.push(i + '%');
        }

        return array;
    }

    var Settings_GetnotificationTimeMs = [
        0, 600000, 1200000, 1800000, 2700000, 3600000, 7200000, 10800000, 14400000, 18000000, 21600000, 25200000, 28800000, 32400000, 36000000,
        39600000, 43200000, 46800000, 50400000, 54000000, 57600000, 61200000, 64800000, 68400000, 72000000, 75600000, 79200000, 82800000, 86400000
    ];

    function Settings_GetnotificationTime(min, hour, hours) {
        var array = [0, '10 ' + min, '20 ' + min, '30 ' + min, '45 ' + min, '1 ' + hour],
            i = 0;

        for (i = 2; i < 25; i++) {
            array.push(i + hours);
        }

        return array;
    }

    function Settings_GetPreviewDelay(ms, sec) {
        var i = 0,
            len = Settings_PreviewDelay.length,
            array = [],
            time;

        for (i; i < len; i++) {
            if (Settings_PreviewDelay[i] < 1000) time = Settings_PreviewDelay[i] + ms;
            else time = Settings_PreviewDelay[i] / 1000 + sec;

            array.push(time);
        }

        return array;
    }

    function Settings_GetBitrates(values) {
        var i = 0,
            len = values.length,
            array = [];

        for (i; i < len; i++) {
            array.push(values[i] + ' Mbps');
        }

        return array;
    }

